pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

interface IPizzaNFT{
    function mint(address _receiver) external;
}

contract PizzaNFT is ERC721, IPizzaNFT{
    address public router;
    uint latestID;

    constructor(address _router)ERC721("ETH PIZZA NFT", "PZA"){
        router = _router;
    }

    function mint(address _receiver) external override{
        require(msg.sender == router, "only the Router can call this function");
        _mint(_receiver, ++latestID);
    }
}","keccak256":"0x2055fdc6926801b56b6bf2fb19b540d882b8a64e1f19c609da5f6d717063dc5a","license":"MIT"},"contracts/PizzaToken.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

interface IPizzaToken{
    function mint(address _receiver, uint _amount) external;
}

contract PizzaToken is ERC20, IPizzaToken{
    address public router;

    constructor(address _router) ERC20("PizzaToken", "PZAT"){
        router = _router;
    }

    function mint(address _receiver, uint _amount) external override{
        require(msg.sender == router, "only the Router can call this function");
